mapper.select:
select	count(*)
from	biz_pr




				and bo_writing = 0

					and (CURRENT_DATE() >= expose_start or expose_start is null)
					and (CURRENT_DATE() <= expose_end or expose_end is null)



				and bo_writing = 1

					or
						(
							bo_writing = 0
							and
							(
								(CURRENT_DATE() < expose_start)
								or 
								(CURRENT_DATE() > expose_end)
							)
						)




			and br_id = #{location}






							and reg_date >= #{startDate}
							and reg_date <= #{endDate}





								and reg_date <= #{endDate}




								and reg_date >= #{startDate}







							and expose_start >= #{startDate}
							and expose_start <= #{endDate}





								and expose_start <= #{endDate}




								and expose_start >= #{startDate}







							and expose_end >= #{startDate}
							and expose_end <= #{endDate}





								and expose_end <= #{endDate}




								and expose_end >= #{startDate}








				and is_writing = 1


				and is_writing = 0

select	*
from	biz_pr




				and bo_writing = 0

					and (CURRENT_DATE() >= expose_start or expose_start is null)
					and (CURRENT_DATE() <= expose_end or expose_end is null)



				and bo_writing = 1

					or
						(
							bo_writing = 0
							and
							(
								(CURRENT_DATE() < expose_start)
								or 
								(CURRENT_DATE() > expose_end)
							)
						)




			and br_id = #{location}






							and reg_date >= #{startDate}
							and reg_date <= #{endDate}





								and reg_date <= #{endDate}




								and reg_date >= #{startDate}







							and expose_start >= #{startDate}
							and expose_start <= #{endDate}





								and expose_start <= #{endDate}




								and expose_start >= #{startDate}







							and expose_end >= #{startDate}
							and expose_end <= #{endDate}





								and expose_end <= #{endDate}




								and expose_end >= #{startDate}








				and is_writing = 1


				and is_writing = 0




order by	pr_id desc
limit		#{pager.startRowIndex}, #{pager.rowsPerPage}

mapper.insert:
insert into biz_pr_sch	(
							pr_id,
							pr_date
						)
values					(
							#{prId},
							#{prDate}
						)

mapper.update:
update biz_pr
br_id = #{brId}, location = #{location}, writing = #{writing}, expose_start = #{exposeStart}, expose_end = #{exposeEnd}
where pr_id = #{prId}
