mapper.select:
select	count(*)
from	branch




				and stts = '작성완료'


				and stts =  '작성중'




			and location like concat('%', #{keyword}, '%')




				and reg_date >= #{startDate}
				and reg_date <= #{endDate}





					and reg_date <= #{endDate}




					and reg_date >= #{startDate}

select	*
from	branch




				and stts = '작성완료'


				and stts =  '작성중'




			and location like concat('%', #{keyword}, '%')




				and reg_date >= #{startDate}
				and reg_date <= #{endDate}





					and reg_date <= #{endDate}




					and reg_date >= #{startDate}





order by	br_id desc
limit		#{pager.startRowIndex}, #{pager.rowsPerPage}

mapper.insert:
insert into		branch_sub	(
								br_id,
								sub_adr,
								sub_name
							)
values						(
								#{brId},
								#{subAdr},
								#{subName}
							)

mapper.update:
update	branch

		location = #{location},
		tel = #{tel},
		adr = #{adr},
		stts = #{stts}

where	br_id = #{brId}
